"use client";

import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bell, HelpCircle, LineChart as LineChartIcon, Settings } from "lucide-react";
import { LineChart, Line, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid } from "recharts";
import moment from "moment";  // To format timestamps

import { Button } from "./ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./ui/card";
import { Select, SelectOption } from "./ui/select";
import { Slider } from "./ui/slider";
import { Switch } from "./ui/switch";

interface DataPoint {
  datetime: string;
  Current: number;
  anomaly: boolean;
}

export default function AnomalyDetectionDashboard() {
  const [darkMode, setDarkMode] = useState(false);
  const [threshold, setThreshold] = useState(0.01);
  const [algorithm, setAlgorithm] = useState("isolation_forest");
  const [dataset, setDataset] = useState("your_dataset.csv");
  const [dataPoints, setDataPoints] = useState<DataPoint[]>([]);
  const [metrics, setMetrics] = useState({
    total_points: 0,
    anomalies_detected: 0,
    average_value: 0,
    max_value: 0,
    min_value: 0,
    last_anomaly_time: null,
  });

  // Fetch data from the backend
  useEffect(() => {
    const interval = setInterval(() => {
      axios.get("http://localhost:5000/api/data")
        .then(response => {
          const { data_points, anomaly_flags } = response.data;
          const combinedData = data_points.map((data: any, index: number) => ({
            datetime: data.datetime,
            Current: data.Current,
            anomaly: anomaly_flags[index] === 1,
          }));
          setDataPoints(combinedData);
        })
        .catch(error => {
          console.error("Error fetching data:", error);
        });

      axios.get("http://localhost:5000/api/metrics")
        .then(response => {
          setMetrics(response.data);
        })
        .catch(error => {
          console.error("Error fetching metrics:", error);
        });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Update settings
  const updateSettings = () => {
    axios.post("http://localhost:5000/api/update_settings", {
      contamination_rate: threshold,
      selected_algorithm: algorithm,
      selected_dataset: dataset,
    })
      .then(response => {
        console.log("Settings updated:", response.data);
      })
      .catch(error => {
        console.error("Error updating settings:", error);
      });
  };

  // Function to filter anomalies
  const handleShowAnomalies = () => {
    const filteredData = dataPoints.filter(point => point.anomaly);
    setDataPoints(filteredData);
  };

  return (
    <div className={`min-h-screen ${darkMode ? "dark bg-gray-900 text-white" : "bg-gray-100 text-gray-900"}`}>
      <header className="bg-white dark:bg-gray-800 shadow-sm">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-10 h-10 bg-gray-200 dark:bg-gray-700 rounded-full" />
            <h1 className="text-2xl font-bold">Real-Time Anomaly Detection Dashboard</h1>
          </div>
          <div className="flex items-center space-x-4">
            <Button variant="ghost" size="icon">
              <Bell className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <HelpCircle className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <Settings className="h-5 w-5" />
            </Button>
            <Switch
              checked={darkMode}
              onCheckedChange={setDarkMode}
              aria-label="Toggle dark mode"
            />
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        {/* First row of cards */}
        <div className="grid gap-6 md:grid-cols-3 lg:grid-cols-3">
          <Card>
            <CardHeader>
              <CardTitle>Total Data Points Processed</CardTitle>
              <LineChartIcon className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.total_points}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Anomalies Detected</CardTitle>
              <Bell className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{metrics.anomalies_detected}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Processing Time per Data Point</CardTitle>
              <Settings className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">0.5ms</div>
            </CardContent>
          </Card>
        </div>

        {/* Second row of cards */}
        <div className="grid gap-6 md:grid-cols-3 lg:grid-cols-3 mt-6">
          <Card>
            <CardHeader>
              <CardTitle>Average Value</CardTitle>
              <Settings className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.average_value.toFixed(2)}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Maximum Value</CardTitle>
              <Settings className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.max_value.toFixed(2)}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Minimum Value</CardTitle>
              <Settings className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.min_value.toFixed(2)}</div>
            </CardContent>
          </Card>
        </div>

        {/* Third row with Last Anomaly Detected */}
        <div className="grid gap-6 md:grid-cols-3 lg:grid-cols-3 mt-6">
          <Card>
            <CardHeader>
              <CardTitle>Last Anomaly Detected Time</CardTitle>
              <Settings className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {metrics.last_anomaly_time ? metrics.last_anomaly_time : "No anomalies detected"}
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="mt-8">
          <Card>
            <CardHeader>
              <CardTitle>Real-Time Data Stream</CardTitle>
              <CardDescription>Live updates of Current values and anomalies</CardDescription>
            </CardHeader>
            <CardContent className="h-[500px]">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={dataPoints}>
                  {/* X-axis with fixed range from 10:00 to 12:00 */}
                  <XAxis 
                    dataKey="datetime"
                    domain={['2020-03-09 10:00:00', '2020-03-09 12:00:00']}  
                    tickFormatter={(tick) => moment(tick).format('HH:mm')}  
                    tick={{ fontSize: 14 }}  
                  />
                  <YAxis 
                    domain={[0.3, 1.7]}  
                    tick={{ fontSize: 14 }} 
                    ticks={[0.3, 0.6, 0.9, 1.2, 1.5, 1.7]}  
                  />                    
                  <CartesianGrid strokeDasharray="3 3" />
                  <Tooltip />
                  <Line 
                    type="monotone" 
                    dataKey="Current" 
                    stroke="#8884d8" 
                    strokeWidth={2} 
                    isAnimationActive={false}  
                  />
                  {dataPoints.filter(point => point.anomaly).map((point, index) => (
                    <Line
                      key={index}
                      type="monotone"
                      dataKey="Current"
                      stroke="#ff0000"
                      strokeWidth={2}
                      dot={{ r: 6, fill: "#ff0000" }}
                      activeDot={{ r: 8 }}
                      data={[point]}
                    />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </div>
      </main>

      <footer className="bg-white dark:bg-gray-800 shadow-sm mt-8">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <p className="text-sm text-gray-600 dark:text-gray-400">
            Â© 2024 Your Company Name. All rights reserved.
          </p>
          <div className="flex space-x-4">
            <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
              Privacy Policy
            </a>
            <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
              Contact Us
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
}
