"use client";

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bell, HelpCircle, LineChart as LineChartIcon, Clock } from 'lucide-react';
import { LineChart, Line, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid } from 'recharts';
import moment from 'moment';

import { Button } from './ui/button';
import {
    Card,
    CardContent,
    CardDescription,
    CardHeader,
    CardTitle,
} from './ui/card';
import { Switch } from './ui/switch';

interface DataPoint {
    datetime: string;
    Current: number;
    anomaly: boolean;
}

export default function AnomalyDetectionDashboard() {
    const [darkMode, setDarkMode] = useState(false);
    const [dataPoints, setDataPoints] = useState<DataPoint[]>([]);
    const [metrics, setMetrics] = useState({
        total_points: 0,
        anomalies_detected: 0,
        average_processing_time: 0.0,
        average_current_value: 0.0,
    });

    // Fetch data from the backend
    useEffect(() => {
        const interval = setInterval(() => {
            axios.get('http://localhost:5000/api/data')
                .then(response => {
                    const { data_points, anomaly_flags } = response.data;
                    const combinedData = data_points.map((data: any, index: number) => ({
                        datetime: data.datetime,
                        Current: data.Current,
                        anomaly: anomaly_flags[index] === 1,
                    }));
                    setDataPoints(combinedData);
                })
                .catch(error => {
                    console.error('Error fetching data:', error);
                });

            axios.get('http://localhost:5000/api/metrics')
                .then(response => {
                    setMetrics(response.data);
                })
                .catch(error => {
                    console.error('Error fetching metrics:', error);
                });
        }, 1000);

        return () => clearInterval(interval);
    }, []);

    // Custom Tooltip to show anomaly and current details
    const CustomTooltip = ({ active, payload }: any) => {
        if (active && payload && payload.length) {
            const { datetime, Current, anomaly } = payload[0].payload;
            return (
                <div className="tooltip bg-white p-2 border border-gray-300 rounded shadow-md">
                    <p><strong>Time:</strong> {datetime}</p>
                    <p><strong>Current:</strong> {Current}</p>
                    <p><strong>Anomaly:</strong> {anomaly ? 'Yes' : 'No'}</p>
                </div>
            );
        }
        return null;
    };

    // Custom Dot Component for anomalies
    const CustomDot = (props: any) => {
        const { cx, cy, payload } = props;
        if (payload.anomaly) {
            return (
                <circle
                    cx={cx}
                    cy={cy}
                    r={6}
                    fill="#ff0000"
                    stroke="#ff0000"
                    strokeWidth={2}
                />
            );
        }
        return null;
    };

    return (
        <div className={`min-h-screen ${darkMode ? "dark bg-gray-900 text-white" : "bg-gray-100 text-gray-900"}`}>
            {/* <header className="bg-white dark:bg-gray-800 shadow-sm">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-10 h-10 bg-gray-200 dark:bg-gray-700 rounded-full" />
            <h1 className="text-2xl font-bold">
              Real-Time Anomaly Detection Dashboard
            </h1>
          </div>
          <div className="flex items-center space-x-4">
            <Button variant="ghost" size="icon">
              <Bell className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <HelpCircle className="h-5 w-5" />
            </Button>
            <Switch
              checked={darkMode}
              onCheckedChange={setDarkMode}
              aria-label="Toggle dark mode"
            />
          </div>
        </div>
      </header> */}

            <main className="container mx-auto px-4 py-8">
                <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
                    <Card className="col-span-full">
                        <CardHeader>
                            <CardTitle>Real-Time Data Stream</CardTitle>
                            <CardDescription>Live updates of Current values and anomalies</CardDescription>
                        </CardHeader>
                        <CardContent className="h-[400px]">
                            <ResponsiveContainer width="100%" height="100%">
                                <LineChart data={dataPoints}>
                                    {/* X-axis with formatted timestamps */}
                                    <XAxis
                                        dataKey="datetime"
                                        tickFormatter={(tick) => moment(tick).format('HH:mm')}
                                        interval="preserveStartEnd"
                                        tick={{ fontSize: 12 }}
                                        angle={-45}
                                        textAnchor="end"
                                    />
                                    {/* Y-axis with Current values */}
                                    <YAxis
                                        domain={[0.3, 1.7]}
                                        tick={{ fontSize: 14 }}
                                        ticks={[0.3, 0.6, 0.9, 1.2, 1.5, 1.7]}
                                    />
                                    <CartesianGrid strokeDasharray="3 3" />
                                    <Tooltip content={<CustomTooltip />} />
                                    <Line
                                        type="monotone"
                                        dataKey="Current"
                                        stroke="#8884d8"
                                        strokeWidth={2}
                                        isAnimationActive={false}
                                        dot={<CustomDot />}
                                    />
                                </LineChart>
                            </ResponsiveContainer>
                        </CardContent>
                    </Card>

                  
                </div>
            </main>

            <footer className="bg-white dark:bg-gray-800 shadow-sm mt-8">
                <div className="container mx-auto px-4 py-4 flex items-center justify-between">
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                        © 2024 Your Company Name. All rights reserved.
                    </p>
                    <div className="flex space-x-4">
                        <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
                            Privacy Policy
                        </a>
                        <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
                            Contact Us
                        </a>
                    </div>
                </div>
            </footer>
        </div>
    );
}
