// src/components/dashboard.tsx

"use client";

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bell, HelpCircle, LineChart as LineChartIcon, Settings, AlertCircle, Clock } from 'lucide-react';
import {
  LineChart,
  Line,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  Scatter,
} from 'recharts';

import { Button } from './ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from './ui/card';
import { Select, SelectOption } from './ui/select';
import { Slider } from './ui/slider';
import { Switch } from './ui/switch';

interface DataPoint {
  time: string;
  value: number;
  anomaly: boolean;
}

export default function AnomalyDetectionDashboard() {
  const [darkMode, setDarkMode] = useState(false);
  const [threshold, setThreshold] = useState(0.01);
  const [algorithm, setAlgorithm] = useState('isolation_forest');
  const [dataset, setDataset] = useState('ambient_temperature_system_failure.csv');
  const [dataPoints, setDataPoints] = useState<DataPoint[]>([]);
  const [metrics, setMetrics] = useState({
    total_points: 0,
    anomalies_detected: 0,
    false_positives: 0,
    false_negatives: 0,
    processing_time: 0,
    detection_rate: 0,
  });

  // Fetch data from the backend
  useEffect(() => {
    const interval = setInterval(() => {
      axios
        .get('http://localhost:5000/api/data')
        .then((response) => {
          const { data_points, anomaly_flags } = response.data;
          const combinedData = data_points.map((item: any, index: number) => ({
            time: item.time || index.toString(),
            value: item.value,
            anomaly: anomaly_flags[index] === 1,
          }));
          setDataPoints(combinedData);
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
        });

      axios
        .get('http://localhost:5000/api/metrics')
        .then((response) => {
          setMetrics(response.data);
        })
        .catch((error) => {
          console.error('Error fetching metrics:', error);
        });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Update settings
  const updateSettings = () => {
    axios
      .post('http://localhost:5000/api/update_settings', {
        contamination_rate: threshold,
        selected_algorithm: algorithm,
        selected_dataset: dataset,
      })
      .then((response) => {
        console.log('Settings updated:', response.data);
      })
      .catch((error) => {
        console.error('Error updating settings:', error);
      });
  };

  return (
    <div
      className={`min-h-screen ${
        darkMode ? 'dark bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'
      }`}
    >
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-sm">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-10 h-10 bg-gray-200 dark:bg-gray-700 rounded-full" />
            <h1 className="text-2xl font-bold">Real-Time Anomaly Detection Dashboard</h1>
          </div>
          <div className="flex items-center space-x-4">
            <Button variant="ghost" size="icon">
              <Bell className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <HelpCircle className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <Settings className="h-5 w-5" />
            </Button>
            <Switch
              checked={darkMode}
              onCheckedChange={setDarkMode}
              aria-label="Toggle dark mode"
            />
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-4 py-8">
        {/* Metrics Cards */}
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {/* Line Chart Card */}
          <Card className="col-span-full mb-6">
            <CardHeader>
              <CardTitle>Real-Time Data Stream</CardTitle>
              <CardDescription>Live updates of data points and anomalies</CardDescription>
            </CardHeader>
            <CardContent className="h-[400px]">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart
                  data={dataPoints}
                  margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
                >
                  <XAxis dataKey="time" stroke="#888888" />
                  <YAxis stroke="#888888" />
                  <CartesianGrid
                    strokeDasharray="3 3"
                    className="stroke-gray-200 dark:stroke-gray-700"
                  />
                  <Tooltip />
                  <Line
                    type="monotone"
                    dataKey="value"
                    stroke="#8884d8"
                    strokeWidth={2}
                    dot={false}
                  />
                  {/* Anomaly Scatter Plot */}
                  <Scatter
                    data={dataPoints.filter((point) => point.anomaly)}
                    fill="red"
                    shape="circle"
                  />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* Total Data Points Processed */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">
                Total Data Points Processed
              </CardTitle>
              <LineChartIcon className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.total_points}</div>
            </CardContent>
          </Card>

          {/* Anomalies Detected */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Anomalies Detected</CardTitle>
              <Bell className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">
                {metrics.anomalies_detected}
              </div>
            </CardContent>
          </Card>

          {/* False Positives */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">False Positives</CardTitle>
              <AlertCircle className="h-4 w-4 text-yellow-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-yellow-600">
                {metrics.false_positives}
              </div>
            </CardContent>
          </Card>

          {/* False Negatives */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">False Negatives</CardTitle>
              <AlertCircle className="h-4 w-4 text-red-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">
                {metrics.false_negatives}
              </div>
            </CardContent>
          </Card>

          {/* Processing Time per Data Point */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">
                Processing Time per Data Point (ms)
              </CardTitle>
              <Clock className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {(metrics.processing_time * 1000).toFixed(2)}
              </div>
            </CardContent>
          </Card>

          {/* Detection Rate */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Detection Rate</CardTitle>
              <LineChartIcon className="h-4 w-4 text-green-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">
                {(metrics.detection_rate * 100).toFixed(2)}%
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Settings Card */}
        <div className="mt-8">
          <Card>
            <CardHeader>
              <CardTitle>Settings</CardTitle>
              <CardDescription>Adjust parameters for anomaly detection</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Threshold Slider */}
              <div className="space-y-1">
                <label htmlFor="threshold" className="text-sm font-medium leading-none">
                  Anomaly Detection Threshold
                </label>
                <Slider
                  id="threshold"
                  min={0.001}
                  max={0.1}
                  step={0.001}
                  value={threshold}
                  onChange={(e) => setThreshold(parseFloat(e.target.value))}
                />
                <p className="text-sm text-gray-500">
                  Current value: {threshold.toFixed(3)}
                </p>
              </div>
              {/* Algorithm Selection */}
              <div className="space-y-1">
                <label htmlFor="algorithm" className="text-sm font-medium leading-none">
                  Algorithm Selection
                </label>
                <Select
                  id="algorithm"
                  value={algorithm}
                  onChange={(e) => setAlgorithm(e.target.value)}
                >
                  <SelectOption value="isolation_forest">Isolation Forest</SelectOption>
                  <SelectOption value="one_class_svm">One-Class SVM</SelectOption>
                  <SelectOption value="local_outlier_factor">
                    Local Outlier Factor
                  </SelectOption>
                  <SelectOption value="elliptic_envelope">Elliptic Envelope</SelectOption>
                </Select>
              </div>
              {/* Dataset Selection */}
              <div className="space-y-1">
                <label htmlFor="dataset" className="text-sm font-medium leading-none">
                  Dataset Selection
                </label>
                <Select
                  id="dataset"
                  value={dataset}
                  onChange={(e) => setDataset(e.target.value)}
                >
                  <SelectOption value="ambient_temperature_system_failure.csv">
                    Ambient Temperature System Failure
                  </SelectOption>
                  <SelectOption value="machine_temperature_system_failure.csv">
                    Machine Temperature System Failure
                  </SelectOption>
                  <SelectOption value="nyc_taxi.csv">NYC Taxi Demand</SelectOption>
                </Select>
              </div>
              <Button onClick={updateSettings}>Apply Changes</Button>
            </CardContent>
          </Card>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white dark:bg-gray-800 shadow-sm mt-8">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <p className="text-sm text-gray-600 dark:text-gray-400">
            Â© 2024 Your Company Name. All rights reserved.
          </p>
          <div className="flex space-x-4">
            <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
              Privacy Policy
            </a>
            <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
              Contact Us
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
}
