"use client";

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bell, HelpCircle, LineChart as LineChartIcon, Settings } from 'lucide-react';
import { LineChart, Line, ResponsiveContainer, Tooltip, XAxis, YAxis, CartesianGrid } from 'recharts';
import moment from 'moment';  // To format timestamps

import { Button } from './ui/button';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from './ui/card';
import { Select, SelectOption } from './ui/select';
import { Slider } from './ui/slider';
import { Switch } from './ui/switch';

interface DataPoint {
  datetime: string;
  Current: number;
  anomaly: boolean;
}

export default function AnomalyDetectionDashboard() {
  const [darkMode, setDarkMode] = useState(false);
  const [threshold, setThreshold] = useState(0.01);
  const [algorithm, setAlgorithm] = useState('isolation_forest');
  const [dataset, setDataset] = useState('your_dataset.csv');
  const [dataPoints, setDataPoints] = useState<DataPoint[]>([]);
  const [metrics, setMetrics] = useState({
    total_points: 0,
    anomalies_detected: 0,
  });

  // Fetch data from the backend
  useEffect(() => {
    const interval = setInterval(() => {
      axios.get('http://localhost:5000/api/data')
        .then(response => {
          const { data_points, anomaly_flags } = response.data;
          const combinedData = data_points.map((data: any, index: number) => ({
            datetime: data.datetime,
            Current: data.Current,
            anomaly: anomaly_flags[index] === 1,
          }));
          setDataPoints(combinedData);
        })
        .catch(error => {
          console.error('Error fetching data:', error);
        });

      axios.get('http://localhost:5000/api/metrics')
        .then(response => {
          setMetrics(response.data);
        })
        .catch(error => {
          console.error('Error fetching metrics:', error);
        });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // Update settings
  const updateSettings = () => {
    axios.post('http://localhost:5000/api/update_settings', {
      contamination_rate: threshold,
      selected_algorithm: algorithm,
      selected_dataset: dataset,
    })
      .then(response => {
        console.log('Settings updated:', response.data);
      })
      .catch(error => {
        console.error('Error updating settings:', error);
      });
  };

  // Function to filter anomalies
  const handleShowAnomalies = () => {
    const filteredData = dataPoints.filter(point => point.anomaly);
    setDataPoints(filteredData);  // Show only anomalies
  };

  return (
    <div className={`min-h-screen ${darkMode ? "dark bg-gray-900 text-white" : "bg-gray-100 text-gray-900"}`}>
      <header className="bg-white dark:bg-gray-800 shadow-sm">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-10 h-10 bg-gray-200 dark:bg-gray-700 rounded-full" />
            <h1 className="text-2xl font-bold">
              Real-Time Anomaly Detection Dashboard
            </h1>
          </div>
          <div className="flex items-center space-x-4">
            <Button variant="ghost" size="icon">
              <Bell className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <HelpCircle className="h-5 w-5" />
            </Button>
            <Button variant="ghost" size="icon">
              <Settings className="h-5 w-5" />
            </Button>
            <Switch
              checked={darkMode}
              onCheckedChange={setDarkMode}
              aria-label="Toggle dark mode"
            />
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <Card className="col-span-full mb-6">
          <CardHeader>
            <CardTitle>Anomaly Detection</CardTitle>
            <CardDescription>True vs Predicted</CardDescription>
          </CardHeader>
          <CardContent className="h-[300px]">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={dataPoints}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis
                  dataKey="timestamp"
                  tickFormatter={(tick) => moment(tick).format('HH:mm')}
                  tick={{ fontSize: 12 }}
                  interval="preserveStartEnd"
                />
                <YAxis domain={[0, 1]} tick={{ fontSize: 12 }} />

                {/* Disable line animations */}
                <Line
                  type="stepAfter"
                  dataKey="true_value"
                  stroke="#007bff"
                  strokeWidth={2}
                  dot={{ r: 3, fill: '#007bff' }}
                  isAnimationActive={false}
                />
                <Line
                  type="stepAfter"
                  dataKey="predicted_value"
                  stroke="#ff7300"
                  strokeWidth={2}
                  dot={{ r: 3, fill: '#ff7300' }}
                  isAnimationActive={false}
                />
                <Tooltip />
                <Legend />
              </LineChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Data Points Processed</CardTitle>
              <LineChartIcon className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{metrics.total_points}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Anomalies Detected</CardTitle>
              <Bell className="h-4 w-4 text-gray-500" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{metrics.anomalies_detected}</div>
            </CardContent>
          </Card>
        </div>

        <div className="mt-8">
          <Card>
            <CardHeader>
              <CardTitle>Settings</CardTitle>
              <CardDescription>Adjust parameters for anomaly detection</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-1">
                <label htmlFor="threshold" className="text-sm font-medium leading-none">
                  Anomaly Detection Threshold
                </label>
                <Slider
                  id="threshold"
                  min={0.001}
                  max={0.1}
                  step={0.001}
                  value={threshold}
                  onChange={(e) => setThreshold(parseFloat(e.target.value))}
                />
                <p className="text-sm text-gray-500">Current value: {threshold.toFixed(3)}</p>
              </div>

              <div className="space-y-1">
                <label htmlFor="algorithm" className="text-sm font-medium leading-none">
                  Algorithm Selection
                </label>
                <Select id="algorithm" value={algorithm} onChange={(e) => setAlgorithm(e.target.value)}>
                  <SelectOption value="isolation_forest">Isolation Forest</SelectOption>
                  <SelectOption value="one_class_svm">One-Class SVM</SelectOption>
                  <SelectOption value="local_outlier_factor">Local Outlier Factor</SelectOption>
                  <SelectOption value="elliptic_envelope">Elliptic Envelope</SelectOption>
                </Select>
              </div>

              <Button onClick={updateSettings}>Apply Changes</Button>
            </CardContent>
          </Card>
        </div>
      </main>

      <footer className="bg-white dark:bg-gray-800 shadow-sm mt-8">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <p className="text-sm text-gray-600 dark:text-gray-400">
            © 2024 Your Company Name. All rights reserved.
          </p>
          <div className="flex space-x-4">
            <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
              Privacy Policy
            </a>
            <a href="#" className="text-sm text-gray-600 dark:text-gray-400 hover:underline">
              Contact Us
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
}
